import React, {useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useParams} from "react-router-dom";
import {Link} from 'react-router-dom';
import Navigate from '../../Components/Navigate';
import Container from "@mui/material/Container";
import Box from "@mui/material/Box";


const Update = (props) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [name, setName] = useState("");
    const [calories, setCalories] = useState("");
    const [proteins, setProteins] = useState("");
    const [fats, setFats] = useState("");
    const [description, setDescription] = useState("");

    useEffect(() => {
        axios.get(`http://localhost:8000/api/update/${id}`)
        .then((res) => {
            setName(res.data.name);
            setCalories(res.data.calories);
            setProteins(res.data.proteins);
            setFats(res.data.fats);
            setDescription(res.data.description);
        })
        .catch(err => console.log(err))
        }, [])

    const updateProduct = (e) => {
            e.preventDefault();
            axios.put(`http://localhost:5000/product/${id}`, {
                name,
                calories,
                proteins,
                fats,
                description,
        })
        .then((res) => {
            console.log(res);
            console.log(res.data);
            navigate("/nutrition");
        })
        .catch((err) => {console.log(err)})
        }


  return (
    <div className='updatewrapper'>
        <Navigate />
        <Container sx={{width: 1000, height: 500, backgroundColor: "white", borderRadius: 5}}>

        </Container>
    </div>
  )
}

export default Update;
